
public class DoWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x = 10;
		
		do {
			System.out.println("x = " + x);
			x--;
		}while(x > 0);
		// D/f b/w whileLoop & doWhileLoop
		//WhileLoop print statement after checking the condition.
		//DowhileLoop executes code once, print statement & then checks for the condition.
		
		}

}
